import { useRef } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import Navbar from "./components/Navbar";
import InfoCards from "./components/InfoCards";
import Services from "./components/Services";
import Appointment from "./components/Appointment";
import Timings from "./components/Timings";
import Footer from "./components/Footer";
import Login from "./components/Login";

import "./App.css";

function App() {
  const homeRef = useRef(null);
  const servicesRef = useRef(null);
  const appointmentRef = useRef(null);
  const timingsRef = useRef(null);

const scrollTo = (ref) => {
  if (!ref) return;

  // wait for DOM to be ready
  setTimeout(() => {
    if (ref.current) {
      ref.current.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  }, 0);
};

  return (
    <Router>
      <Navbar
        scrollTo={scrollTo}
        refs={{
          homeRef,
          servicesRef,
          appointmentRef,
          timingsRef,
        }}
      />

      <Routes>
        {/* MAIN SINGLE PAGE */}
        <Route
          path="/"
          element={
            <div className="scroll-container">
              <section ref={homeRef} className="page">
                <h1>SmileCare Dental Clinic</h1>
                <p>Your smile is our priority</p>
                <InfoCards />
              </section>

              <section ref={servicesRef} className="page">
                <Services />
              </section>

              <section ref={appointmentRef} className="page">
                <Appointment />
              </section>

              <section ref={timingsRef} className="page">
                <Timings />
              </section>

              <Footer />
            </div>
          }
        />

        {/* ADMIN LOGIN PAGE */}
        <Route path="/login" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
