import { useEffect, useState } from "react";
import API from "../services/api"; // ‚≠ê use your axios instance

function ViewAppointments() {
  const [appointments, setAppointments] = useState([]);
  const isAdmin = localStorage.getItem("isAdmin") === "true";

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await API.get("/appointments"); // ‚≠ê cleaner
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    if (isAdmin) {
      fetchAppointments();
    }
  }, [isAdmin]);

  if (!isAdmin) {
    return (
      <div style={{ padding: "30px", color: "red" }}>
        <h3>‚ö†Ô∏è Please login as admin to view appointments</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: "30px" }}>
      <h2>üìã Booked Appointments</h2>

      {appointments.length === 0 ? (
        <p>No appointments yet</p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Date</th>
            </tr>
          </thead>

          <tbody>
            {appointments.map((a) => (
              <tr key={a._id}>
                <td>{a.name}</td>
                <td>{a.phone}</td>
                <td>{new Date(a.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ViewAppointments;
