import { useNavigate, useLocation } from "react-router-dom";

function Navbar({ scrollTo, refs }) {
  const navigate = useNavigate();
  const location = useLocation();

  const handleNav = (ref) => {
    if (location.pathname !== "/") {
      // go back to home first
      navigate("/");
      // wait for home to load, then scroll
      setTimeout(() => scrollTo(ref), 100);
    } else {
      scrollTo(ref);
    }
  };

  return (
    <nav className="navbar">
      <div className="nav-logo">SmileCare</div>

      <div className="nav-links">
        <button onClick={() => handleNav(refs.homeRef)}>Home</button>
        <button onClick={() => handleNav(refs.servicesRef)}>Services</button>
        <button onClick={() => handleNav(refs.appointmentRef)}>Appointment</button>
        <button onClick={() => handleNav(refs.timingsRef)}>Timings</button>

        <button onClick={() => navigate("/login")} className="login-link">
          Admin Login
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
