import { useState } from "react";
import "./Navbar.css";
import logo from "../assets/logo.jpeg";

function Navbar({ isAdmin, onLoginClick, onLogoutClick }) {
  const [menuOpen, setMenuOpen] = useState(false);

  const scrollToSection = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: "smooth" });
    setMenuOpen(false); // close menu after click (mobile)
  };

  return (
    <nav className="navbar">
      {/* LEFT: LOGO */}
      <div className="navbar-left" onClick={() => scrollToSection("home")}>
        <img src={logo} alt="Gaviranga Logo" className="navbar-logo" />
        <span className="brand-text">Gaviranga Dental</span>
      </div>

      {/* HAMBURGER ICON (MOBILE) */}
      <div
        className={`hamburger ${menuOpen ? "open" : ""}`}
        onClick={() => setMenuOpen(!menuOpen)}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      {/* RIGHT: NAV MENU */}
      <div className={`navbar-right ${menuOpen ? "active" : ""}`}>
        <button onClick={() => scrollToSection("home")}>Home</button>
        <button onClick={() => scrollToSection("services")}>Services</button>
        <button onClick={() => scrollToSection("appointment")}>
          Appointment
        </button>

        {!isAdmin ? (
          <button className="admin-btn" onClick={onLoginClick}>
            Admin Login
          </button>
        ) : (
          <button className="logout-btn" onClick={onLogoutClick}>
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
