import { useRef, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

import Navbar from "./components/Navbar";
import InfoCards from "./components/InfoCards";
import Services from "./components/Services";
import Appointment from "./components/Appointment";
import Timings from "./components/Timings";
import Footer from "./components/Footer";
import Login from "./components/Login";

import "./App.css";

/* ---------- HOME PAGE COMPONENT ---------- */
function HomePage() {
  const homeRef = useRef(null);
  const servicesRef = useRef(null);
  const appointmentRef = useRef(null);
  const timingsRef = useRef(null);

  const location = useLocation();

  const sectionMap = {
    home: homeRef,
    services: servicesRef,
    appointment: appointmentRef,
    timings: timingsRef,
  };

  // scroll AFTER navigation
  useEffect(() => {
    if (location.state?.scrollTo) {
      const ref = sectionMap[location.state.scrollTo];
      if (ref?.current) {
        ref.current.scrollIntoView({ behavior: "smooth" });
      }
    }
  }, [location]);

  return (
    <div className="scroll-container">
      <section ref={homeRef} className="page">
        <h1>SmileCare Dental Clinic</h1>
        <p>Your smile is our priority</p>
        <InfoCards />
      </section>

      <section ref={appointmentRef} className="page">
        <Appointment />
      </section>

      <section ref={timingsRef} className="page">
        <Timings />
      </section>

      <section ref={servicesRef} className="page">
        <Services />
      </section>

      <Footer />
    </div>
  );
}

/* ---------- APP ---------- */
function App() {
  return (
    <Router>
      <Navbar />

      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
