import { useState } from "react";
import API from "../services/api";
import "./Appointment.css";

export default function Appointment() {

  const [form, setForm] = useState({
    name: "",
    phone: "",
    date: ""
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });


  // ⭐ FIXED: direct click handler (no form submit issues)
  const handleSubmit = async () => {

    console.log("Button clicked:", form); // debug

    try {
      const res = await API.post("/appointments", form);

      console.log("Saved:", res.data);

      alert("Appointment booked successfully ✅");

      setForm({ name: "", phone: "", date: "" });

    } catch (err) {
      console.error("POST ERROR:", err.response || err);
      alert("Error booking");
    }
  };


  return (
    <section className="appointment-section">
      <h2>Book Appointment</h2>

      {/* ⭐ removed onSubmit to avoid issues */}
      <form className="appointment-form">

        <input
          type="text"
          name="name"
          placeholder="Enter your name"
          value={form.name}
          onChange={handleChange}
          required
        />

        <input
          type="tel"
          name="phone"
          placeholder="Enter phone number"
          value={form.phone}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          required
        />

        {/* ⭐ IMPORTANT: use button click */}
        <button type="button" onClick={handleSubmit}>
          Book Appointment
        </button>

      </form>
    </section>
  );
}
