const handleSubmit = async () => {

  const payload = {
    name: form.name.trim(),
    phone: form.phone.trim(),
    date: form.date
  };

  try {
    // ⭐ capture response
    const res = await API.post("/appointments", payload);

    // ⭐ SHOW TOKEN HERE
    alert(`✅ Appointment booked!\nYour Token Number: ${res.data.token}`);

    setForm({ name: "", phone: "", date: "" });

  } catch (err) {
    console.error(err);
    alert("❌ Error booking appointment");
  }
};
