import { useState } from "react";
import API from "../services/api";
import "./Appointment.css";

export default function Appointment({
  showAdminLogin,
  setShowAdminLogin,
  onAdminLoginSuccess   // ‚úÖ receive from App.js
}) {

  const [form, setForm] = useState({
    name: "",
    phone: "",
    date: ""
  });

  /* ===============================
     HANDLE CHANGE
  =============================== */
  const handleChange = (e) => {
    const { name, value } = e.target;

    // ‚≠ê NAME: allow max 30 characters
    if (name === "name" && value.length > 30) return;

    // ‚≠ê PHONE: digits only, max 10
    if (name === "phone") {
      const digitsOnly = value.replace(/\D/g, "");
      if (digitsOnly.length > 10) return;
      setForm(prev => ({ ...prev, phone: digitsOnly }));
      return;
    }

    // ‚≠ê block Sunday
    if (name === "date") {
      const day = new Date(value).getDay();
      if (day === 0) {
        alert("‚ùå Clinic Closed on Sunday. Please select another day.");
        return;
      }
    }

    setForm(prev => ({ ...prev, [name]: value }));
  };

  /* ===============================
     APPOINTMENT SUBMIT
  =============================== */
  const handleSubmit = async () => {
    if (!form.name || !form.phone || !form.date) {
      alert("Please fill all fields");
      return;
    }

    if (form.phone.length !== 10) {
      alert("Phone number must be exactly 10 digits");
      return;
    }

    try {
      const res = await API.post("/appointments", {
        name: form.name.trim(),
        phone: form.phone.trim(),
        date: form.date
      });

      alert(`‚úÖ Appointment booked!\nToken Number: ${res.data.token}`);
      setForm({ name: "", phone: "", date: "" });

    } catch (err) {
      alert(err.response?.data?.error || "Error booking");
    }
  };

  /* ===============================
     ADMIN LOGIN
  =============================== */
  const handleAdminLogin = () => {
    // üîê replace with API validation later
    onAdminLoginSuccess();    // ‚úÖ correct flow
    setShowAdminLogin(false);
    alert("Admin logged in successfully");
  };

  const today = new Date().toISOString().split("T")[0];

  return (
    <section id="appointment" className="appointment-section">

      {!showAdminLogin ? (
        <>
          <h2>Book Appointment</h2>

          <div className="appointment-form">
            <input
              name="name"
              placeholder="Enter your name"
              value={form.name}
              maxLength={30}
              onChange={handleChange}
            />

            <input
              name="phone"
              placeholder="Enter phone number"
              value={form.phone}
              maxLength={10}
              onChange={handleChange}
            />

            <input
              type="date"
              name="date"
              min={today}
              value={form.date}
              onChange={handleChange}
            />

            <button onClick={handleSubmit}>
              Book Appointment
            </button>
          </div>
        </>
      ) : (
        <>
          <h2>Admin Login</h2>

          <div className="appointment-form">
            <input type="text" placeholder="Admin Username" />
            <input type="password" placeholder="Password" />

            <button onClick={handleAdminLogin}>
              Login
            </button>

            <p
              style={{ marginTop: "10px", cursor: "pointer", color: "#0a3d62" }}
              onClick={() => setShowAdminLogin(false)}
            >
              ‚Üê Back to Appointment
            </p>
          </div>
        </>
      )}

    </section>
  );
}
