import { useState } from "react";
import API from "../services/api";
import "./Appointment.css";

export default function Appointment() {

  const [form, setForm] = useState({
    name: "",
    phone: "",
    date: ""
  });

  /* ===============================
     HANDLE CHANGE
  =============================== */
  const handleChange = (e) => {
    const { name, value } = e.target;

    // ⭐ NAME: allow max 30 characters
    if (name === "name") {
      if (value.length > 30) return;
    }

    // ⭐ PHONE: allow only digits & max 10 digits
    if (name === "phone") {
      const digitsOnly = value.replace(/\D/g, "");
      if (digitsOnly.length > 10) return;

      setForm(prev => ({
        ...prev,
        phone: digitsOnly
      }));
      return;
    }

    // ⭐ block Sunday
    if (name === "date") {
      const day = new Date(value).getDay(); // 0 = Sunday
      if (day === 0) {
        alert("❌ Clinic Closed on Sunday. Please select another day.");
        return;
      }
    }

    setForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  /* ===============================
     SUBMIT
  =============================== */
  const handleSubmit = async () => {

    if (!form.name || !form.phone || !form.date) {
      alert("Please fill all fields");
      return;
    }

    if (form.phone.length !== 10) {
      alert("Phone number must be exactly 10 digits");
      return;
    }

    try {
      const res = await API.post("/appointments", {
        name: form.name.trim(),
        phone: form.phone.trim(),
        date: form.date
      });

      const token = res.data.token;
      alert(`✅ Appointment booked!\nToken Number: ${token}`);

      setForm({ name: "", phone: "", date: "" });

    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Error booking");
    }
  };

  /* ===============================
     TODAY DATE
  =============================== */
  const today = new Date().toISOString().split("T")[0];

  return (
    <section id="appointment" className="appointment-section">
      <h2>Book Appointment</h2>

      <div className="appointment-form">

        <input
          name="name"
          placeholder="Enter your name"
          value={form.name}
          maxLength={30}        // ⭐ UI-level limit
          onChange={handleChange}
        />

        <input
          name="phone"
          placeholder="Enter phone number"
          value={form.phone}
          maxLength={10}        // ⭐ UI-level limit
          onChange={handleChange}
        />

        <input
          type="date"
          name="date"
          min={today}
          value={form.date}
          onChange={handleChange}
        />

        <button type="button" onClick={handleSubmit}>
          Book Appointment
        </button>

      </div>
    </section>
  );
}
